package week1.day1_20th;

public class PrintOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num =2;
		
		for (int i = 0; i<20 ; i++) {
			if(i%2!=0)
			{
				System.out.println(i+"The number is odd");
		} 
			else {
				System.out.println(i+"Not an odd number");
			}
			}
	}

}
